import { createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { API } from "../../helpers/consts";
import { getConfig } from "../../helpers/functions";

export const getProducts = createAsyncThunk(
  "products/getProducts",
  async () => {
    try {
      const result = await axios.get(
        `${API}/v1/freelancer/` + window.location.search,
        getConfig()
      );
      return result.data;
    } catch (error) {
      throw error;
    }
  }
);

export const getCategories = createAsyncThunk(
  "products/getCategories",
  async () => {
    try {
      const result = await axios.get(`${API}/v1/category/`, getConfig());
      return result.data.results;
    } catch (error) {
      throw error;
    }
  }
);

export const createProduct = createAsyncThunk(
  "products/createProduct",
  async (newProduct) => {
    try {
      await axios.post(`${API}/v1/freelancer/`, newProduct, getConfig());
    } catch (error) {
      throw error;
    }
  }
);

export const deleteProduct = createAsyncThunk(
  "products/deleteProduct",
  async (id, thunkAPI) => {
    try {
      await axios.delete(`${API}/v1/freelancer/${id}/`, getConfig());
      thunkAPI.dispatch(getProducts());
    } catch (error) {
      throw error;
    }
  }
);

export const getOneProduct = createAsyncThunk(
  "products/getOneProduct",
  async (id) => {
    try {
      const result = await axios.get(
        `${API}/v1/freelancer/${id}/`,
        getConfig()
      );

      return result.data;
    } catch (error) {
      throw error;
    }
  }
);

export const updateProduct = createAsyncThunk(
  "products/updateProduct",
  async (editedProduct) => {
    try {
      await axios.patch(
        `${API}/v1/freelancer/${editedProduct.get("id")}/`,
        editedProduct,
        getConfig()
      );
    } catch (error) {
      throw error;
    }
  }
);
