import React from "react";
import { Button } from "react-bootstrap";
import "../index.css";
import { useDispatch, useSelector } from "react-redux";
import { useSearchParams } from "react-router-dom";
import { getCategories } from "../store/actions/freelancerActions";

const Filter = () => {
  const { productList, loading, categories } = useSelector(
    (state) => state.product
  );
  const dispatch = useDispatch();
  const [searchParams, setSearchParams] = useSearchParams();
  useEffect(() => {
    dispatch(getProducts());
    dispatch(getCategories());
  }, [searchParams]);
  const navigate = useNavigate();
  const filterByCategory = (query, value) => {
    const search = new URLSearchParams(window.location.search);

    if (value === "all") {
      search.delete(query);
    } else {
      search.set(query, value);
    }

    const url = `${window.location.pathname}?${search.toString()}`;
    navigate(url);
  };

  return (
    <div className="block-filter">
      <div className="filter-content">
        <h2>Filter</h2>
        <span className="line-filter"></span>
        <Button className="filter-btns">Frontend Developer</Button>
        <Button className="filter-btns">Web</Button>
        <Button className="filter-btns">UX/UI</Button>
        <Button className="filter-btns">Backend Developer</Button>
      </div>
    </div>
  );
};

export default Filter;
