import { createSlice } from "@reduxjs/toolkit";
import { login, logout, register } from "../actions/authActions";
import {
  loginFreeLance,
  logoutFreeLance,
  registerFreelance,
} from "../actions/authActionsFreeLance";

const initialState = {
  currentUser: localStorage.getItem("email"),
  error: null,
  loading: false,
};

export const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    setCurrentUser: (state) => {
      state.currentUser = null;
    },
  },
  extraReducers: {
    [registerFreelance.pending]: (state) => {
      state.loading = true;
    },
    [registerFreelance.fulfilled]: (state) => {
      state.loading = false;
    },
    [registerFreelance.rejected]: (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    },
    [loginFreeLance.fulfilled]: (state, action) => {
      state.loading = false;
      state.currentUser = action.payload;
    },
    [loginFreeLance.pending]: (state) => {
      state.loading = true;
    },
    [loginFreeLance.rejected]: (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    },

    [logoutFreeLance.fulfilled]: (state) => {
      state.currentUser = null;
    },
  },
});

export const { setCurrentUser } = authSlice.actions;
export const authReducer = authSlice.reducer;
