import { createSlice } from "@reduxjs/toolkit";
import { login, logout, register } from "../actions/authActions";
import {
  getCategories,
  loginFreeLance,
  logoutFreeLance,
  registerFreelance,
} from "../actions/authActionsFreeLance";
import RegisterFreelancer from "../../components/auth/RegisterFreelancer";
import LoginFreelancer from "../../components/auth/LoginFreelancer";

const initialState = {
  currentUser: localStorage.getItem("email"),
  error: null,
  categories: [],
  loading: false,
};

export const authSliceFreeLance = createSlice({
  name: "authFreelancer",
  initialState,
  reducers: {
    setCurrentUserFree: (state) => {
      state.currentUser = null;
    },
  },
  extraReducers: {
    [RegisterFreelancer.pending]: (state) => {
      state.loading = true;
    },
    [RegisterFreelancer.fulfilled]: (state) => {
      state.loading = false;
    },
    [RegisterFreelancer.rejected]: (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    },
    [getCategories.pending]: (state) => {
      state.loading = true;
    },
    [getCategories.fulfilled]: (state, action) => {
      state.loading = false;
      state.categories = action.payload;
    },
    [getCategories.rejected]: (state) => {
      state.loading = false;
    },
    [LoginFreelancer.fulfilled]: (state, action) => {
      state.loading = false;
      state.currentUser = action.payload;
    },
    [LoginFreelancer.pending]: (state) => {
      state.loading = true;
    },
    [LoginFreelancer.rejected]: (state, action) => {
      state.loading = false;
      state.error = action.error.message;
    },

    [logoutFreeLance.fulfilled]: (state) => {
      state.currentUser = null;
    },
  },
});

export const { setCurrentUser } = authSliceFreeLance.actions;
export const authReducer = authSliceFreeLance.reducer;
